    private void clearFields() {
        txtName.setText("");
        txtWeight.setText("");
        txtHeight.setText("");
    }
    
    private void updateTable(){
        list = dao.listName(txtSearch.getText());
        ((DefaultTableModel) tableIMC.getModel()).setNumRows(0);
        tableIMC.updateUI();
        DefaultTableModel model = (DefaultTableModel) tableIMC.getModel();
        for(User st:list) {
            model.addRow(new Object[]{st.getName(), st.getWeight(), st.getHeight(), st.getIMC(), st.getRating()});
        }
    }
    
    private void btnCalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculateActionPerformed
        if (txtName.getText().isEmpty() || txtWeight.getText().isEmpty() || txtHeight.getText().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Os campos não podem retornar vazios");
        } else {
            try {
                User user = new User();
                user.setName(txtName.getText());
                user.setGender(cbxGender.getSelectedItem().toString());
                user.setWeight(Double.parseDouble(txtWeight.getText().replace(",", ".")));
                user.setHeight(Double.parseDouble(txtHeight.getText().replace(",", ".")));

                dao.calculate(user);
                dao.add(user);

                JOptionPane.showMessageDialog(null, "Usuário " + txtName.getText() + " inserido com sucesso!");
                clearFields();
            } catch (NumberFormatException e) {
                JOptionPane.showMessageDialog(null, "Peso e Altura devem ser números válidos");
            }
        }
        
        clearFields();
    }//GEN-LAST:event_btnCalculateActionPerformed

    private void btnSearchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSearchActionPerformed
        updateTable();
    }//GEN-LAST:event_btnSearchActionPerformed

