    private void btnDeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDeleteActionPerformed
        int linha = tableIMC.getSelectedRow();

        if (linha == -1) {
            JOptionPane.showMessageDialog(null, "Selecione um item na tabela.");
        } else {
            String name = tableIMC.getValueAt(linha, 0).toString(); 

            dao.delete(name);

            JOptionPane.showMessageDialog(null, "Registro removido.");
            updateTable();
        }
    }//GEN-LAST:event_btnDeleteActionPerformed

    private void btnUpdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnUpdateActionPerformed
        int linha = tableIMC.getSelectedRow();

        if (linha == -1) {
            JOptionPane.showMessageDialog(null, "Selecione um item na tabela.");
            return;
        }

        if (txtName.getText().isEmpty() || txtWeight.getText().isEmpty() || txtHeight.getText().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Nenhum campo pode estar vazio.");
            return;
        }

        try {
            String name = tableIMC.getValueAt(linha, 0).toString();

            User user = new User();
            user.setName(txtName.getText());
            user.setWeight(Double.parseDouble(txtWeight.getText().replace(",", ".")));
            user.setHeight(Double.parseDouble(txtHeight.getText().replace(",", ".")));
            user.setGender(cbxGender.getSelectedItem().toString());

            dao.update(name, user);
            JOptionPane.showMessageDialog(null, "Registro alterado com sucesso!");
            updateTable();
            clearFields();
        } catch (NumberFormatException ex) {
            JOptionPane.showMessageDialog(null, "Verifique se o peso e altura são números válidos.");
        } catch (Exception ex) {
            JOptionPane.showMessageDialog(null, "Erro ao atualizar: " + ex.getMessage());
        }
    }//GEN-LAST:event_btnUpdateActionPerformed

